#![feature(test)]

extern crate test;
use error_correction_number_1_hamming_code::{decode, encode};
use test::{black_box, Bencher};

#[bench]
fn bench_encode(bencher: &mut Bencher) {
    bencher.iter(|| {
        encode(black_box(if cfg!(miri) {
            "The Sensei"
        } else {
            "The Sensei told me that i can do this kata"
        }))
    });
}

#[bench]
fn bench_decode(bencher: &mut Bencher) {
    bencher.iter(|| decode(black_box(if cfg!(miri) { "000111000111000111000100000111111000111000001000" } else { "" })));
}
